function clean( content ) {
  return content; // TODO
}

function users( sockets, self ) {
  var users = []

  Object.keys(sockets.sockets).forEach(function(key){
    if( sockets.sockets[key].id != self.id )
      users.push(sockets.sockets[key].username)
  })

  return users;
}

function toString( users ) {
  if (users > 1)
    return `, ${users.join(',')} are online!`
  else if (users == 1)
    return `, ${users.first} is online!`
}

exports.clean = clean;
exports.users = users;
exports.toString = toString;
