const fs = require('fs');

function clean( content ) {
  return content; // TODO
}

function log(id, name, action, data) {
  console.log(`[${id}, ${name}, ${action}, ${Object.values(data)}]`)

  var obj = {
    id: id,
    name: name,
    action: action,
    data: data,
    datetime: new Date().toISOString()
  }

  fs.appendFile(`/share/messages.json`, JSON.stringify(obj), function (err) {
    if (err) throw err;
  });
}

exports.clean = clean;
exports.log = log;
